import type { Document } from '.mongodb-68UHUK8p/src/bson';
import type { Collection } from '.mongodb-68UHUK8p/src/collection';
import type { Server } from '.mongodb-68UHUK8p/src/sdam/server';
import type { ClientSession } from '.mongodb-68UHUK8p/src/sessions';
import { type TimeoutContext } from '.mongodb-68UHUK8p/src/timeout';
import { AbstractOperation } from '.mongodb-68UHUK8p/src/operations/operation';

/**
 * @public
 */
export interface SearchIndexDescription extends Document {
  /** The name of the index. */
  name?: string;

  /** The index definition. */
  definition: Document;

  /** The type of the index.  Currently `search` or `vectorSearch` are supported. */
  type?: string;
}

/** @internal */
export class CreateSearchIndexesOperation extends AbstractOperation<string[]> {
  constructor(
    private readonly collection: Collection,
    private readonly descriptions: ReadonlyArray<SearchIndexDescription>
  ) {
    super();
  }

  override get commandName() {
    return 'createSearchIndexes' as const;
  }

  override async execute(
    server: Server,
    session: ClientSession | undefined,
    timeoutContext: TimeoutContext
  ): Promise<string[]> {
    const namespace = this.collection.fullNamespace;
    const command = {
      createSearchIndexes: namespace.collection,
      indexes: this.descriptions
    };

    const res = await server.command(namespace, command, {
      session,
      timeoutContext
    });

    const indexesCreated: Array<{ name: string }> = res?.indexesCreated ?? [];
    return indexesCreated.map(({ name }) => name);
  }
}
