import type { Document } from '.mongodb-68UHUK8p/src/bson';
import type { Collection } from '.mongodb-68UHUK8p/src/collection';
import { MONGODB_ERROR_CODES, MongoServerError } from '.mongodb-68UHUK8p/src/error';
import type { Server } from '.mongodb-68UHUK8p/src/sdam/server';
import type { ClientSession } from '.mongodb-68UHUK8p/src/sessions';
import { type TimeoutContext } from '.mongodb-68UHUK8p/src/timeout';
import { AbstractOperation } from '.mongodb-68UHUK8p/src/operations/operation';

/** @internal */
export class DropSearchIndexOperation extends AbstractOperation<void> {
  constructor(
    private readonly collection: Collection,
    private readonly name: string
  ) {
    super();
  }

  override get commandName() {
    return 'dropSearchIndex' as const;
  }

  override async execute(
    server: Server,
    session: ClientSession | undefined,
    timeoutContext: TimeoutContext
  ): Promise<void> {
    const namespace = this.collection.fullNamespace;

    const command: Document = {
      dropSearchIndex: namespace.collection
    };

    if (typeof this.name === 'string') {
      command.name = this.name;
    }

    try {
      await server.command(namespace, command, { session, timeoutContext });
    } catch (error) {
      const isNamespaceNotFoundError =
        error instanceof MongoServerError && error.code === MONGODB_ERROR_CODES.NamespaceNotFound;
      if (!isNamespaceNotFoundError) {
        throw error;
      }
    }
  }
}
